  static List<String> _createPrintJobs(String printType) {
    final lowerPrintType = printType.toLowerCase();
    switch (lowerPrintType) {
      case 'customer':
        return ['customer'];
      case 'kitchen':
        return ['kitchen'];
      case 'bar':
        return ['bar'];
      case 'waiter':
        return ['waiter'];
      case 'kitchen_and_bar':
        return ['kitchen', 'bar'];
      case 'all':
        return ['customer', 'kitchen', 'bar', 'waiter'];
      default:
        return [];
    }
  }

