  static Future<List<int>> generateLogoBytess(
    Generator generator,
    String imagePath,
    PaperSize paperSize,
  ) async {
    final List<int> bytes = [];

    try {
      final ByteData byteData = await rootBundle.load(imagePath);
      final Uint8List imageBytes = byteData.buffer.asUint8List();

      final image = img.decodeImage(imageBytes)!;

      // Resize gambar sesuai lebar kertas
      final resizedImage = img.copyResize(image, width: 300);

      // Konversi ke grayscale
      final grayscaleImage = img.grayscale(resizedImage);

      bytes.addAll(generator.image(grayscaleImage));
      debugPrint('Logo image loaded and processed successfully.');
      bytes.addAll(generator.feed(1));
    } catch (e) {
      debugPrint('Error loading logo image: $e');
      // Fallback: kalau gambar gagal, cetak nama toko saja
      bytes.addAll(
        generator.text(
          'Baraja Amphitheater',
          styles: const PosStyles(
            align: PosAlign.center,
            bold: true,
            height: PosTextSize.size2,
          ),
        ),
      );
      bytes.addAll(generator.feed(1));
    }

    return bytes;
  }

